// q0038_count_and_say 


struct Solution;

const N2C: [char; 9] = ['1','2','3','4','5','6','7','8','9'];

impl Solution {
    pub fn count_and_say(n: i32) -> String {
        if n <= 1 {
            return String::from("1");
        }
        let lastv = Solution::count_and_say(n-1);
        let mut ret = String::new();
        let mut lastc = '0';
        let mut ccount = 0;
        for c in lastv.chars() {
            if lastc == '0' {
                lastc = c;
                ccount += 1;
                continue;
            }
            if lastc == c {
                ccount += 1;
            }else {
                ret.push(N2C[ccount-1]);
                ret.push(lastc);
                lastc = c;
                ccount = 1;
            }
        }
        ret.push(N2C[ccount-1]);
        ret.push(lastc);
        ret
    }
}



